```javascript
// GeoJSON Point format
// Important: Coordinates are [longitude, latitude]
location: {
  type: "Point",
  coordinates: [-73.856077, 40.848447] // [longitude, latitude]
}

// Find theaters within a circular area
db.theaters.find({
    "location.geo": {
        $geoWithin: {
            $centerSphere: [
                [-73.9857, 40.7484], // [longitude, latitude] - Times Square
                10 / 3963.2 // 10 miles radius (converted to radians)
            ]
        }
    }
