        }
    },
    {
        $project: {
            theaterName: "$name",
            address: "$location.address",
            restaurantCount: { $size: "$nearbyRestaurants" },
            nearbyRestaurants: {
                $map: {
                    input: "$nearbyRestaurants",
                    as: "restaurant",
                    in: {
                        name: "$$restaurant.name",
                        cuisine: "$$restaurant.cuisine",
                        rating: "$$restaurant.rating",
                        distance: {
