    {
        $lookup: {
            from: "theaters",
            let: { restaurantLocation: "$location.geo" },
            pipeline: [
                {
                    $geoNear: {
                        near: "$$restaurantLocation",
                        distanceField: "distanceFromRestaurant",
                        maxDistance: 500 // 500m walking distance
                    }
