const bcrypt = require('bcrypt');
const mongoose = require('mongoose');
const { Schema } = mongoose;

// User schema with password hashing middleware
const userSchema = new Schema({
    name: { type: String, required: true },
    email: { type: String, required: true, unique: true },
    password: { type: String, required: true, minlength: 6 },
    role: { type: String, enum: ['user', 'admin'], default: 'user' },
    loginAttempts: { type: Number, default: 0 },
    lastLogin: Date
}, {
    timestamps: true
});

// PRE SAVE MIDDLEWARE - Runs BEFORE saving
userSchema.pre('save', async function(next) {
    console.log('Pre-save middleware triggered');

    // 'this' refers to the document being saved
    if (!this.isModified('password')) {
        console.log('Password not modified, skipping hash');
        return next(); // If password hasn't changed, skip hashing
    }

    try {
        console.log('Hashing password...');
        const salt = await bcrypt.genSalt(10);
        this.password = await bcrypt.hash(this.password, salt);
        console.log('Password hashed successfully');
        next(); // Continue with save operation
    } catch (error) {
        console.error('Error hashing password:', error);
        next(error); // Pass error to abort save
    }
});

// PRE SAVE MIDDLEWARE - Validate email format
userSchema.pre('save', function(next) {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(this.email)) {
        const error = new Error('Invalid email format');
        return next(error);
    }
    next();
});

// POST SAVE MIDDLEWARE - Runs AFTER successful save
userSchema.post('save', function(doc, next) {
    console.log('Post-save middleware triggered');
    console.log(`User ${doc.name} has been saved with ID: ${doc._id}`);

    // Send welcome email (async operation)
    sendWelcomeEmail(doc.email, doc.name)
        .then(() => {
            console.log('Welcome email sent');
            next();
        })
        .catch(error => {
            console.error('Failed to send welcome email:', error);
            // Don't pass error to next() - save already succeeded
            next();
        });
});

// PRE UPDATE MIDDLEWARE - Runs before update operations
userSchema.pre(['updateOne', 'findOneAndUpdate'], async function(next) {
    const update = this.getUpdate();

    // If password is being updated, hash it
    if (update.$set && update.$set.password) {
        try {
            const salt = await bcrypt.genSalt(10);
            update.$set.password = await bcrypt.hash(update.$set.password, salt);
            console.log('Password hashed in update middleware');
        } catch (error) {
            return next(error);
        }
    }

    next();
});

// POST DELETE MIDDLEWARE - Cleanup after deletion
userSchema.post('findOneAndDelete', async function(doc) {
    if (doc) {
        console.log(`User ${doc.name} deleted, cleaning up...`);

        // Remove user's posts
        await mongoose.model('Post').deleteMany({ author: doc._id });

        // Log deletion for audit
        await mongoose.model('AuditLog').create({
            action: 'USER_DELETED',
            userId: doc._id,
            userName: doc.name,
            deletedAt: new Date()
        });

        console.log('Cleanup completed');
    }
});

// Instance methods
userSchema.methods.comparePassword = async function(candidatePassword) {
    return bcrypt.compare(candidatePassword, this.password);
};

userSchema.methods.incrementLoginAttempts = function() {
    this.loginAttempts += 1;
    return this.save();
};

userSchema.methods.resetLoginAttempts = function() {
    this.loginAttempts = 0;
    this.lastLogin = new Date();
    return this.save();
};

// Static methods
userSchema.statics.findByEmail = function(email) {
    return this.findOne({ email: email.toLowerCase() });
};

const User = mongoose.model('User', userSchema);

// Usage examples
async function createUser() {
    try {
        const user = new User({
            name: 'John Doe',
            email: 'john@example.com',
            password: 'plaintext123' // Will be hashed by pre-save middleware
        });

        const savedUser = await user.save();
        console.log('User created:', savedUser.name);
        // Password is now hashed in database

    } catch (error) {
        console.error('Error creating user:', error.message);
    }
}

async function loginUser(email, password) {
    try {
        const user = await User.findByEmail(email);
        if (!user) {
            throw new Error('User not found');
        }

        const isMatch = await user.comparePassword(password);
        if (!isMatch) {
            await user.incrementLoginAttempts();
            throw new Error('Invalid password');
        }

        await user.resetLoginAttempts();
        return user;

    } catch (error) {
        console.error('Login error:', error.message);
        throw error;
    }
}

// Helper function (would be in separate module)
async function sendWelcomeEmail(email, name) {
    // Simulate email sending
    return new Promise((resolve) => {
        setTimeout(() => {
            console.log(`Welcome email sent to ${email}`);
            resolve();
        }, 100);
    });
}
```

**Middleware Types Comparison:**

| Type | When it Runs | Use Cases | Error Handling |
|------|--------------|-----------|----------------|
| **PRE** | Before operation | Validation, hashing, data transformation | Call `next(error)` to abort |
| **POST** | After successful operation | Logging, notifications, cleanup | Errors don't affect operation |

**Common Middleware Hooks:**

| Hook | Triggers On | Example Use Case |
|------|-------------|------------------|
| **`pre('save')`** | `save()`, `create()` | Hash passwords, validate data |
| **`post('save')`** | After save success | Send notifications, log events |
| **`pre('remove')`** | `remove()` | Check dependencies, validate deletion |
| **`post('remove')`** | After removal | Cleanup related data |
| **`pre('updateOne')`** | Update operations | Hash passwords in updates |
| **`pre('find')`** | Query operations | Add default filters |

**Best Practices:**
- **Use `this.isModified()`** to check if field changed
- **Handle errors properly** in pre middleware
- **Don't fail post middleware** - operation already succeeded
- **Use middleware for** validation, hashing, logging, cleanup
- **Keep middleware focused** - one responsibility per hook

```javascript
// Population is Mongoose's way of handling relationships
// It automatically replaces specified paths with actual documents from other collections

// 1. Define schemas with references
const userSchema = new Schema({
    name: String,
    email: String,
    profile: {
        bio: String,
        avatar: String
    }
});

const postSchema = new Schema({
    title: String,
    content: String,
    author: {
        type: Schema.Types.ObjectId,
        ref: 'User', // This tells Mongoose which model to use during population
        required: true
    },
    tags: [String],
    comments: [{
        author: {
            type: Schema.Types.ObjectId,
            ref: 'User'
        },
        text: String,
        createdAt: { type: Date, default: Date.now }
    }]
}, { timestamps: true });

const User = mongoose.model('User', userSchema);
const Post = mongoose.model('Post', postSchema);

// 2. Basic population
const post = await Post.findById('some-post-id')
    .populate('author', 'name email'); // Populate 'author' and select only name and email

console.log(post.author.name); // Instead of just an ID, post.author is now the full user document

// 3. Advanced population with options
const postsWithAuthors = await Post.find()
    .populate({
        path: 'author',
        select: 'name profile.avatar email',
        match: { 'profile.verified': true }, // Only populate verified users
        options: { sort: { name: 1 } }
    })
    .populate({
        path: 'comments.author',
        select: 'name profile.avatar'
    })
    .sort({ createdAt: -1 })
    .limit(10);

// 4. Conditional population
const userPosts = await Post.find({ author: userId })
    .populate({
        path: 'author',
        select: 'name email',
        match: { active: true },
        populate: {
            path: 'department', // Nested population
            select: 'name location'
        }
    });

// 5. Population with aggregation-like features
const popularPosts = await Post.find()
    .populate({
        path: 'comments.author',
        select: 'name profile.reputation',
        options: {
            sort: { 'profile.reputation': -1 },
            limit: 5 // Only top 5 commenters by reputation
        }
    })
    .where('comments.5').exists() // Posts with at least 6 comments
    .sort({ 'comments.length': -1 });

// 6. Virtual population (reverse populate)
userSchema.virtual('posts', {
    ref: 'Post',
    localField: '_id',
    foreignField: 'author'
});

// Enable virtual fields in JSON output
userSchema.set('toJSON', { virtuals: true });

const userWithPosts = await User.findById(userId)
    .populate({
        path: 'posts',
        select: 'title createdAt',
        options: { sort: { createdAt: -1 }, limit: 10 }
    });

// 7. Manual population using aggregation
const manualPopulation = await Post.aggregate([
    { $match: { author: mongoose.Types.ObjectId(userId) } },
    {
        $lookup: {
            from: 'users',
            localField: 'author',
            foreignField: '_id',
            as: 'authorDetails'
        }
    },
    {
        $unwind: '$authorDetails'
    },
    {
        $project: {
            title: 1,
            content: 1,
            'author.name': '$authorDetails.name',
            'author.email': '$authorDetails.email',
            createdAt: 1
        }
    }
]);
```

**Population Features:**

| Feature | Description | Example |
|---------|-------------|--------|
| **Basic Population** | Replace ObjectId with referenced document | `.populate('author')` |
| **Field Selection** | Choose specific fields to populate | `.populate('author', 'name email')` |
| **Conditional Population** | Filter populated documents | `match: { active: true }` |
| **Nested Population** | Populate references within populated docs | `populate: { path: 'department' }` |
| **Virtual Population** | Reverse populate (one-to-many) | Virtual fields with ref |

**How Population Works:**
1. **Storage**: Only ObjectId is stored in the document
2. **Query Time**: Mongoose performs separate queries to fetch referenced documents
3. **Replacement**: ObjectIds are replaced with actual documents
4. **Performance**: Multiple queries behind the scenes (consider aggregation for complex cases)

```javascript
// Mongoose provides Model.aggregate() method to use MongoDB's aggregation pipeline
// The aggregation framework processes documents in a series of stages

// Sample User schema for demonstration
const userSchema = new Schema({
    name: String,
    email: String,
    age: Number,
    role: { type: String, enum: ['user', 'editor', 'admin'] },
    department: String,
    salary: Number,
    joinDate: Date,
    isActive: { type: Boolean, default: true },
    skills: [String],
    projects: [{
        name: String,
        status: { type: String, enum: ['active', 'completed', 'cancelled'] },
        budget: Number,
        startDate: Date
    }]
});

const User = mongoose.model('User', userSchema);

// 1. Basic aggregation - Calculate user statistics by role
const roleStats = await User.aggregate([
    // Stage 1: Filter only active users
    {
        $match: { isActive: true }
    },
    // Stage 2: Group documents by the 'role' field
    {
        $group: {
            _id: '$role', // The field to group by
            userCount: { $sum: 1 }, // Count documents in each group
            averageAge: { $avg: '$age' }, // Calculate the average age in each group
            averageSalary: { $avg: '$salary' },
            minSalary: { $min: '$salary' },
            maxSalary: { $max: '$salary' },
            totalSalaryBudget: { $sum: '$salary' }
        }
    },
    // Stage 3: Sort the results by user count descending
    {
        $sort: { userCount: -1 }
    },
    // Stage 4: Reshape the output
    {
        $project: {
            _id: 0, // Exclude the default _id field
            role: '$_id', // Rename _id to role
            count: '$userCount',
            avgAge: { $round: ['$averageAge', 1] }, // Round the average age
            avgSalary: { $round: ['$averageSalary', 2] },
            salaryRange: {
                min: '$minSalary',
                max: '$maxSalary'
            },
            totalBudget: '$totalSalaryBudget'
        }
    }
]);

// 2. Complex aggregation with multiple stages
const departmentAnalysis = await User.aggregate([
    // Stage 1: Match active users with projects
    {
        $match: {
            isActive: true,
            'projects.0': { $exists: true } // Has at least one project
        }
    },
    // Stage 2: Unwind projects array
    {
        $unwind: '$projects'
    },
    // Stage 3: Add computed fields
    {
        $addFields: {
            projectDuration: {
                $dateDiff: {
                    startDate: '$projects.startDate',
                    endDate: new Date(),
                    unit: 'day'
                }
            },
            experienceYears: {
                $dateDiff: {
                    startDate: '$joinDate',
                    endDate: new Date(),
                    unit: 'year'
                }
            }
        }
    },
    // Stage 4: Group by department
    {
        $group: {
            _id: '$department',
            employeeCount: { $sum: 1 },
            avgAge: { $avg: '$age' },
            avgExperience: { $avg: '$experienceYears' },
            avgSalary: { $avg: '$salary' },
            totalProjects: { $sum: 1 },
            activeProjects: {
                $sum: {
                    $cond: [{ $eq: ['$projects.status', 'active'] }, 1, 0]
                }
            },
            completedProjects: {
                $sum: {
                    $cond: [{ $eq: ['$projects.status', 'completed'] }, 1, 0]
                }
            },
            totalBudget: { $sum: '$projects.budget' },
            avgProjectDuration: { $avg: '$projectDuration' },
            topSkills: { $addToSet: '$skills' } // Collect unique skills
        }
    },
    // Stage 5: Add calculated fields
    {
        $addFields: {
            completionRate: {
                $round: [
                    {
                        $multiply: [
                            { $divide: ['$completedProjects', '$totalProjects'] },
                            100
                        ]
                    },
                    2
                ]
            },
            avgProjectBudget: {
                $round: [{ $divide: ['$totalBudget', '$totalProjects'] }, 2]
            }
        }
    },
    // Stage 6: Sort by total budget descending
    {
        $sort: { totalBudget: -1 }
    },
    // Stage 7: Final projection
    {
        $project: {
            department: '$_id',
            employeeCount: 1,
            avgAge: { $round: ['$avgAge', 1] },
            avgExperience: { $round: ['$avgExperience', 1] },
            avgSalary: { $round: ['$avgSalary', 2] },
            projectStats: {
                total: '$totalProjects',
                active: '$activeProjects',
                completed: '$completedProjects',
                completionRate: '$completionRate'
            },
            budgetStats: {
                total: '$totalBudget',
                avgPerProject: '$avgProjectBudget'
            },
            avgProjectDuration: { $round: ['$avgProjectDuration', 0] },
            skillCount: { $size: { $reduce: {
                input: '$topSkills',
                initialValue: [],
                in: { $setUnion: ['$$value', '$$this'] }
            }}},
            _id: 0
        }
    }
]);

// 3. Aggregation with lookup (join)
const userProjectSummary = await User.aggregate([
    { $match: { isActive: true } },
    {
        $lookup: {
            from: 'projects', // External collection
            localField: '_id',
            foreignField: 'assignedUsers',
            as: 'assignedProjects'
        }
    },
    {
        $addFields: {
            projectCount: { $size: '$assignedProjects' },
            activeProjectCount: {
                $size: {
                    $filter: {
                        input: '$assignedProjects',
                        cond: { $eq: ['$$this.status', 'active'] }
                    }
                }
            }
        }
    },
    {
        $project: {
            name: 1,
            role: 1,
