```javascript
// $explain and $hint are powerful tools for query optimization
// $explain shows how MongoDB executes queries
// $hint forces MongoDB to use specific indexes

// Sample collection setup for demonstration
db.orders.insertMany([
    { orderId: 1, customerId: 101, status: "pending", amount: 250.00, date: new Date("2024-01-15") },
    { orderId: 2, customerId: 102, status: "completed", amount: 175.50, date: new Date("2024-01-16") },
    { orderId: 3, customerId: 101, status: "pending", amount: 320.75, date: new Date("2024-01-17") },
    // ... more documents
]);

// Create various indexes for demonstration
db.orders.createIndex({ status: 1 }); // Single field index
db.orders.createIndex({ customerId: 1, date: -1 }); // Compound index
db.orders.createIndex({ amount: 1 }); // Amount index
db.orders.createIndex({ status: 1, amount: -1 }); // Status + amount compound

// 1. Using $explain - Basic Query Analysis
// Three verbosity levels: queryPlanner, executionStats, allPlansExecution

// queryPlanner (default) - Shows winning query plan
db.orders.find({ status: "pending" }).explain();

// executionStats - Shows execution statistics
const executionStats = db.orders.find({ status: "pending" }).explain("executionStats");
/*
Key metrics in executionStats:
- totalDocsExamined: Documents scanned
- totalDocsReturned: Documents returned
- executionTimeMillis: Query execution time
- indexesUsed: Which indexes were utilized
- stage: IXSCAN (index scan) vs COLLSCAN (collection scan)
*/

// allPlansExecution - Shows all considered plans
db.orders.find({ status: "pending" }).explain("allPlansExecution");

// 2. Complex Query Analysis
const complexQuery = db.orders.find({
    status: "pending",
    amount: { $gte: 200 },
    customerId: { $in: [101, 102, 103] }
}).sort({ date: -1 }).explain("executionStats");

// Analyze the results
function analyzeQueryPerformance(explainResult) {
    const stats = explainResult.executionStats;

    console.log("Query Performance Analysis:");
    console.log(`Execution Time: ${stats.executionTimeMillis}ms`);
    console.log(`Documents Examined: ${stats.totalDocsExamined}`);
    console.log(`Documents Returned: ${stats.totalDocsReturned}`);
    console.log(`Index Used: ${stats.winningPlan.inputStage?.indexName || 'None (Collection Scan)'}`);

    // Performance indicators
    const ratio = stats.totalDocsReturned / stats.totalDocsExamined;
    if (ratio < 0.1) {
        console.log("‚ö†Ô∏è  Warning: Low selectivity ratio - consider better indexing");
    }

    if (stats.executionTimeMillis > 100) {
        console.log("‚ö†Ô∏è  Warning: Query taking too long - optimization needed");
    }

    if (stats.winningPlan.stage === 'COLLSCAN') {
        console.log("‚ö†Ô∏è  Warning: Collection scan detected - create appropriate index");
    }
}

// 3. Using $hint to Force Specific Index Usage

// Without hint - MongoDB chooses the index
db.orders.find({ 
    status: "pending", 
    amount: { $gte: 200 } 
}).explain("executionStats");

// With hint - Force use of status index
db.orders.find({ 
    status: "pending", 
    amount: { $gte: 200 } 
}).hint({ status: 1 }).explain("executionStats");

// With hint - Force use of compound index
db.orders.find({ 
    status: "pending", 
    amount: { $gte: 200 } 
}).hint({ status: 1, amount: -1 }).explain("executionStats");

// Force collection scan (for comparison)
db.orders.find({ status: "pending" }).hint({ $natural: 1 }).explain("executionStats");

// 4. Performance Testing and Comparison
function compareIndexPerformance() {
    const query = { status: "pending", amount: { $gte: 200 } };

    console.log("Comparing different index strategies:");

    // Test 1: No hint (MongoDB's choice)
    const noHint = db.orders.find(query).explain("executionStats");
    console.log(`No Hint: ${noHint.executionStats.executionTimeMillis}ms`);

    // Test 2: Status index
    const statusHint = db.orders.find(query).hint({ status: 1 }).explain("executionStats");
    console.log(`Status Index: ${statusHint.executionStats.executionTimeMillis}ms`);

    // Test 3: Compound index
    const compoundHint = db.orders.find(query).hint({ status: 1, amount: -1 }).explain("executionStats");
    console.log(`Compound Index: ${compoundHint.executionStats.executionTimeMillis}ms`);

    // Test 4: Amount index
    const amountHint = db.orders.find(query).hint({ amount: 1 }).explain("executionStats");
    console.log(`Amount Index: ${amountHint.executionStats.executionTimeMillis}ms`);
}

// 5. Aggregation Pipeline Explain
const aggregationExplain = db.orders.aggregate([
    { $match: { status: "pending" } },
    { $group: { 
        _id: "$customerId", 
        totalAmount: { $sum: "$amount" },
        orderCount: { $sum: 1 }
    }},
    { $sort: { totalAmount: -1 } },
    { $limit: 10 }
]).explain("executionStats");

// 6. Index Effectiveness Analysis
function analyzeIndexEffectiveness(collectionName) {
    // Get index usage statistics
    const indexStats = db[collectionName].aggregate([
        { $indexStats: {} }
    ]).toArray();

    console.log("Index Usage Statistics:");
    indexStats.forEach(index => {
        console.log(`Index: ${index.name}`);
        console.log(`Usage Count: ${index.accesses.ops}`);
        console.log(`Last Used: ${index.accesses.since}`);

        if (index.accesses.ops === 0) {
            console.log(`‚ö†Ô∏è  Index '${index.name}' is unused - consider dropping`);
        }
    });
}

// 7. Query Optimization Recommendations
function optimizeQuery(query, collection) {
    const explainResult = db[collection].find(query).explain("executionStats");
    const stats = explainResult.executionStats;

    const recommendations = [];

    // Check for collection scan
    if (stats.winningPlan.stage === 'COLLSCAN') {
        recommendations.push("Create an index on frequently queried fields");
    }

    // Check selectivity
    const selectivity = stats.totalDocsReturned / stats.totalDocsExamined;
    if (selectivity < 0.1) {
        recommendations.push("Improve query selectivity - consider compound indexes");
    }

    // Check execution time
    if (stats.executionTimeMillis > 100) {
        recommendations.push("Query is slow - consider index optimization");
    }

    // Check if sort is in memory
    if (stats.winningPlan.inputStage?.stage === 'SORT') {
        recommendations.push("Sort operation not using index - create index with sort fields");
    }

    return recommendations;
}

// 8. Mongoose Integration
