<!-- JavaScript-controlled progress -->
<button onclick="updateProgress()">Start Download</button>
<br><br>
<label for="download">Download Progress:</label>
<progress id="download" value="0" max="100">0%</progress>
<span id="progress-text">0%</span>

<script>
let downloadProgress = 0;
function updateProgress() {
  const progressBar = document.getElementById('download');
  const progressText = document.getElementById('progress-text');

  const interval = setInterval(() => {
    downloadProgress += 10;
    progressBar.value = downloadProgress;
    progressText.textContent = downloadProgress + '%';

    if (downloadProgress >= 100) {
      clearInterval(interval);
      progressText.textContent = 'Complete!';
    }
  }, 200);
}
</script>

