  }

  // Drag event handlers
  handleDragStart(e) {
    this.draggedElement = e.target;
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/html', e.target.outerHTML);
    e.dataTransfer.setData('text/plain', e.target.dataset.item);

    // Add visual feedback
    e.target.style.opacity = '0.5';

    console.log('Drag started:', e.target.dataset.item);
  }

  handleDragEnd(e) {
    e.target.style.opacity = '1';
    this.draggedElement = null;
  }

  handleDragOver(e) {
    e.preventDefault(); // Allow drop
    e.dataTransfer.dropEffect = 'move';
  }

  handleDragEnter(e) {
    e.preventDefault();
    e.target.classList.add('dragover');
  }

  handleDragLeave(e) {
    // Only remove class if we're leaving the drop zone entirely
    if (!e.target.contains(e.relatedTarget)) {
      e.target.classList.remove('dragover');
    }
  }

  handleDrop(e) {
    e.preventDefault();
    e.target.classList.remove('dragover');

    const itemData = e.dataTransfer.getData('text/plain');
    const zoneData = e.target.dataset.zone;

    if (this.draggedElement) {
      // Clone the dragged element
      const clone = this.draggedElement.cloneNode(true);
      clone.style.opacity = '1';

      // Add to drop zone
      e.target.appendChild(clone);

      console.log(`Dropped ${itemData} into ${zoneData}`);

      // Optional: Remove original element
      // this.draggedElement.remove();
    }
  }

  // Sortable list implementation
  initSortableList() {
    const sortableList = document.getElementById('sortable-list');
    let draggedItem = null;

    sortableList.addEventListener('dragstart', (e) => {
      draggedItem = e.target;
      e.target.style.opacity = '0.5';
    });

    sortableList.addEventListener('dragend', (e) => {
      e.target.style.opacity = '1';
      draggedItem = null;
    });

    sortableList.addEventListener('dragover', (e) => {
      e.preventDefault();
      const afterElement = this.getDragAfterElement(sortableList, e.clientY);
      if (afterElement == null) {
        sortableList.appendChild(draggedItem);
      } else {
        sortableList.insertBefore(draggedItem, afterElement);
      }
    });
  }

  getDragAfterElement(container, y) {
    const draggableElements = [...container.querySelectorAll('li:not(.dragging)')];

    return draggableElements.reduce((closest, child) => {
      const box = child.getBoundingClientRect();
      const offset = y - box.top - box.height / 2;

      if (offset < 0 && offset > closest.offset) {
        return { offset: offset, element: child };
      } else {
        return closest;
      }
    }, { offset: Number.NEGATIVE_INFINITY }).element;
  }

  // File drop zone implementation
  initFileDropZone() {
    const fileDropZone = document.getElementById('file-drop-zone');
    const fileInput = document.getElementById('file-input');
    const fileList = document.getElementById('file-list');

    // Click to select files
    fileDropZone.addEventListener('click', () => {
      fileInput.click();
    });

    // File input change
    fileInput.addEventListener('change', (e) => {
      this.handleFiles(e.target.files);
    });

    // Prevent default drag behaviors
    ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
      fileDropZone.addEventListener(eventName, (e) => {
        e.preventDefault();
        e.stopPropagation();
      });
    });

    // Highlight drop zone when item is dragged over it
    ['dragenter', 'dragover'].forEach(eventName => {
      fileDropZone.addEventListener(eventName, () => {
        fileDropZone.classList.add('dragover');
      });
    });

    ['dragleave', 'drop'].forEach(eventName => {
      fileDropZone.addEventListener(eventName, () => {
        fileDropZone.classList.remove('dragover');
      });
    });

    // Handle dropped files
    fileDropZone.addEventListener('drop', (e) => {
      const files = e.dataTransfer.files;
      this.handleFiles(files);
    });
  }

  handleFiles(files) {
    const fileList = document.getElementById('file-list');
    fileList.innerHTML = '<h4>Selected Files:</h4>';

    [...files].forEach(file => {
      const fileItem = document.createElement('div');
      fileItem.innerHTML = `
        <p><strong>${file.name}</strong></p>
        <p>Size: ${(file.size / 1024).toFixed(2)} KB</p>
        <p>Type: ${file.type || 'Unknown'}</p>
        <hr>
      `;
      fileList.appendChild(fileItem);
    });
  }
}

// Initialize drag and drop functionality
document.addEventListener('DOMContentLoaded', () => {
  new DragDropManager();
});
</script>

<style>
