  interactiveCtx.lineCap = 'round';
  interactiveCtx.strokeStyle = currentColor;
  interactiveCtx.lineTo(x, y);
  interactiveCtx.stroke();
  interactiveCtx.beginPath();
  interactiveCtx.moveTo(x, y);
});

interactiveCanvas.addEventListener('mouseup', () => {
