}

// Canvas Animation
const animationCanvas = document.getElementById('animation-canvas');
const animationCtx = animationCanvas.getContext('2d');

let ballX = 50;
let ballY = 100;
let ballSpeedX = 3;
let ballSpeedY = 2;
const ballRadius = 20;

function animate() {
  // Clear canvas
  animationCtx.clearRect(0, 0, animationCanvas.width, animationCanvas.height);

  // Update ball position
  ballX += ballSpeedX;
  ballY += ballSpeedY;

  // Bounce off walls
  if (ballX + ballRadius > animationCanvas.width || ballX - ballRadius < 0) {
    ballSpeedX = -ballSpeedX;
  }
  if (ballY + ballRadius > animationCanvas.height || ballY - ballRadius < 0) {
    ballSpeedY = -ballSpeedY;
  }

  // Draw ball
  animationCtx.beginPath();
  animationCtx.arc(ballX, ballY, ballRadius, 0, 2 * Math.PI);
  animationCtx.fillStyle = 'lightcoral';
  animationCtx.fill();
  animationCtx.strokeStyle = 'red';
  animationCtx.stroke();

  // Continue animation
  requestAnimationFrame(animate);
}

