    &:hover {
      color: lighten(@primary-color, 20%);
    }
  }
}

// Mixins
.button-style(@bg-color; @text-color: white) {
  background: @bg-color;
  color: @text-color;
  padding: 0.5rem 1rem;

  &:hover {
    background: darken(@bg-color, 10%);
  }
}

// Using mixins
